mapper.select:
select

from d_bbs
where 1 = 1

  and id = #{id}


  and bbs_userId = #{bbsUserid}


  and bbs_content like "%"#{bbsContent}"%"


  and bbs_status = #{bbsStatus}


  and create_time = #{createTime}

order by create_time desc

mapper.update:
update d_bbs
set bbs_userId = #{bbsUserid,jdbcType=INTEGER},
  bbs_content = #{bbsContent,jdbcType=VARCHAR},
  bbs_status = #{bbsStatus,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
