mapper.select:
select

from d_bulletin
where 1 = 1

    and id = #{id}


    and bulletin_userId = #{bulletinUserid}


    and bulletin_title like "%"#{bulletinTitle}"%"


    and bulletin_status = #{bulletinStatus}


    and create_time = #{createTime}


    and bulletin_content like "%"#{bulletinContent}"%"

order by create_time desc

mapper.update:
update d_bulletin
set bulletin_userId = #{bulletinUserid,jdbcType=INTEGER},
    bulletin_title  = #{bulletinTitle,jdbcType=VARCHAR},
    bulletin_status = #{bulletinStatus,jdbcType=INTEGER},
    create_time     = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
