mapper.select:
select d.id, d.product_name, d.product_category, d.product_status, d.create_time, d.product_introduction, i.id as did, i.product_id, i.img, i.create_time from d_product d inner join d_img i on d.id = i.product_id where 1 = 1
and d.id = #{id}
and d.product_name like "%"#{productName}"%"
and d.product_status = #{productStatus}
and d.create_time like "%"#{createTime}"%"
and d.product_introduction like "%"#{productIntroduction}"%"

mapper.update:
update d_product
set product_name     = #{productName,jdbcType=VARCHAR},
    product_category = #{productCategory,jdbcType=INTEGER},
    product_status   = #{productStatus,jdbcType=INTEGER},
    create_time      = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
